import React, { useState } from 'react';
import { Formik } from 'formik';
import api from '../services/api';

export default function Register() {

  const [ apiErrors, setApiErrors ] = useState('');

  return (
    <div className="register-content">
      <div className="register-form">
        <Formik
          initialValues={{ name: '', email: '', password: '' }}
          validate={values => {
            const errors = {};
            if (!values.name) {
              errors.name = 'Required';
            } else if (!values.email) {
              errors.email = 'Required';
            } else if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = 'Invalid email address';
            }
            return errors;
          }}
          onSubmit={(values, { setSubmitting, isSubmitting }) => {
            setSubmitting(true);
            api.post("auth/register", {
              name: values.name,
              email: values.email,
              password: values.password
            })
              .then((response) => {
                debugger
                setSubmitting(false);
              })
              .catch((err) => {
                debugger
                console.log(err);
                setApiErrors(err.response.data.error);
                console.error("ops! ocorreu um erro" + err);
              });
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
          }) => (
            <>
              <form onSubmit={handleSubmit}>
              { apiErrors && <span>apiErrors</span> }
                <input
                  placeholder="Name"
                  type="name"
                  name="name"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.name}
                  required={errors.name && touched.name && errors.name}
                />
                <input
                  placeholder="Email"
                  type="email"
                  name="email"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.email}
                  required={errors.email && touched.email && errors.email}
                />
                <input
                  placeholder="Password"
                  type="password"
                  name="password"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.password}
                  required={errors.password && touched.password && errors.password}
                />
                {isSubmitting ?
                  <button>
                    <i className="fa fa-circle-o-notch fa-spin"></i>Loading
                  </button>
                  :
                  <button type="submit" disabled={isSubmitting}>
                    Submit
                  </button>
                }
              </form>
            </>
          )}

        </Formik>
      </div>
    </div>
  )
}
