import React from 'react';
import { Formik } from 'formik';

export default function Register() {
  return (
    <div className="register-content">
      <div className="register-form">
        <Formik
          initialValues={{ name: '', email: '', password: '' }}
          validate={values => {
            const errors = {};
            if (!values.name) {
              errors.name = 'Required';
            } else if (!values.email) {
              errors.email = 'Required';
            } else if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = 'Invalid email address';
            }
            return errors;
          }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
          }) => (
            <form onSubmit={handleSubmit}>
              <input
                className={ errors.name && touched.name && errors.name ? "required-input" : ''}
                placeholder="Name"
                type="name"
                name="name"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.name}
                required={errors.name && touched.name && errors.name}
              />
              {/* <span className="required-alert">{errors.name && touched.name && errors.name}</span> */}
              <input
                placeholder="Email"
                type="email"
                name="email"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.email}
              />
              <span className="required-alert">{errors.email && touched.email && errors.email}</span>
              <input
                placeholder="Password"
                type="password"
                name="password"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.password}
              />
              <span className="required-alert">{errors.password && touched.password && errors.password}</span>
              <button className="submit-button" type="submit" disabled={isSubmitting}>
                Submit
              </button>
            </form>

          )}

        </Formik>
      </div>
    </div>
  )
}
